    //Login Student button
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        
         String mobile=jTextField1.getText();
        String email=jTextField2.getText();
        Student info=checkBrowerInfo(mobile,email);
        if(info==null){
            JOptionPane.showMessageDialog(this, "Eror in Mobile or Email");
            jTextField1.setText("");
            jTextField2.setText("");
        }else{
        //if informations are correct go to student board
        StudentBoard a=new StudentBoard(info);
        a.setVisible(true);
        }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

