    //Register button for student
    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        String s=borrInfo();
        try{
            PrintWriter pw=new PrintWriter(new File("Borrowers.csv"));
            
            String line=jTextField3.getText()+","+jTextField5.getText()+","+jTextField4.getText()+","+jComboBox1.getSelectedItem().toString()+"\n";
            //add in excel sheet
            pw.append(s+line);
            pw.close();
            JOptionPane.showMessageDialog(this, "Register Success");
            jTextField3.setText("");
            jTextField4.setText("");
            jTextField5.setText("");
        }catch(Exception ex){
            JOptionPane.showMessageDialog(this, ex);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

    //this method used in register
    String borrInfo(){
         String s="";
    try{
            Scanner sc=new Scanner(new File("Borrowers.csv"));
            String line=sc.next();
            s+=line;
            while(sc.hasNext()){
                line=sc.nextLine();
                s+=line+"\n";
            
            }
          sc.close();
        }catch(Exception ex){
            JOptionPane.showMessageDialog(this, ex);
        }
    return s;
    
    }
    
