    //to check borrower info when login
    //user==mobile number
    Student checkBrowerInfo(String user,String email){
        Student s=null;
        try {
            Scanner sc = new Scanner(new File("Borrowers.csv"));
            while (sc.hasNext()) {
                String line = sc.nextLine();
                String parts[] = line.split(",");
                if(parts[1].equals(user) && parts[2].equals(email))
                {
                 sc.close();
                 s=new Student();
                 s.setName(parts[0]);
                 s.setEmail(parts[2]);
                 s.setMobile(parts[1]);
                 s.setLevel(Integer.parseInt(parts[3]));
                 
                 break;
                }
            }
            sc.close(); 
        } catch (Exception ex) {
            System.out.println(ex);
        }
        return s;
    }
